import React, { useState, useEffect } from "react";
import './Blog.css'

const BASE_URL = 'http://localhost:8000/'

function Blog({blog}) {
    const [imageUrl, setImageUrl] = useState('')

    useEffect(() => {
        setImageUrl(BASE_URL + blog.image)
    }, [])

    return (
        <div className="blog">
            <img className="blog_image" src={imageUrl}/>
        </div>
    )

}

export default Blog
